mapper.select:
select param_value from crawler_action_target_param
where action_id=#{actionId}
and param_type=#{paramType}
and param_name=#{paramName}

mapper.insert:
insert into crawler_action_target_param(action_id,param_type,param_name,param_value)
values (#{actionId},#{paramType},#{paramName},#{paramValue})
on duplicate key update param_value=#{paramValue};
